package Java_Operators;

public class Relational_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Equality(==)
		int a = 5;
		int b = 10;
		System.out.println(a==b);
		
		//Inequality(!=)
		System.out.println(a!=b);
		
		//Less than(<)
		System.out.println(a<b);
		
		//Greater than (>)
		System.out.println(a>b);
		
		//Less than or equal to (<=)
		System.out.println(a<=b);
		
		//Greater than or equal to (>=)
		System.out.println(a>=b);
	}

}
