package Java_Variables;

public class Local_Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/**
		 * Local variables are crucial for encapsulation, preventing unintended program interactions, and efficiently managing memory resources as they are automatically deallocated when they go out of scope.
		 */
		//Declared a Local Variable
		int var = 10;
		
		//This variable is local to this main method only
		System.out.println("Local Variable: "+var);
		
	}

}
